mapper.select:
  		select  p.prdIndex, p.prdCode, p.prdName, p.prdPrice, p.prdRname from product_tb p left join Size_Tb s on p.prdIndex = s.prdIndex
  		where p.prdIndex = s.prdIndex
  		and p.prdCode like '%#{searchType}%'

  			and p.prdPrice BETWEEN #{priceMin} AND #{priceMax}


		and ( 1 = 1

			or s.sizeKind = #{sizeKind}

		)


		and ( 1 = 1

			or p.prdCode = '__#{prdType}%'

		)

  		GROUP BY p.prdIndex

  		select count(DISTINCT p.prdIndex) as total from product_tb p left join Size_Tb s on p.prdIndex = s.prdIndex
  		where p.prdIndex = s.prdIndex
  		and p.prdCode like '%#{searchType}%'

  			and p.prdPrice BETWEEN #{priceMin} AND #{priceMax}


		and ( 1 = 1

			or s.sizeKind = #{sizeKind}

		)


		and ( 1 = 1

			or p.prdCode = '__#{prdType}%'

		)
