mapper.select:
select p.prdIndex, p.prdCode, p.prdName, p.prdPrice, p.prdRname, SUM(oi.orderItemStock) as totalSales from product_tb p left join Size_Tb s on p.prdIndex = s.prdIndex LEFT JOIN Order_Item_Tb oi ON s.sizeIndex = oi.sizeIndex where p.prdIndex = s.prdIndex and p.prdCode like concat('%',#{searchType},'%')
and ( 1 != 1
or s.sizeKind = #{size}
)
and ( 1 != 1
or p.prdCode LIKE concat('__', #{Type}, '%')
)
GROUP BY p.prdIndex
order by
p.prdDate desc totalSales desc p.prdPrice p.prdPrice desc
limit #{start}, #{perPage}

  		select count(DISTINCT p.prdIndex) as total from product_tb p left join Size_Tb s on p.prdIndex = s.prdIndex
  		where p.prdIndex = s.prdIndex
  		and p.prdCode like concat('%',#{searchType},'%')

  			and p.prdPrice BETWEEN #{priceMin, jdbcType=INTEGER} AND #{priceMax, jdbcType=INTEGER}


		and ( 1 != 1

			or s.sizeKind = #{size}

		)


		and ( 1 != 1

			or p.prdCode LIKE concat('__', #{Type}, '%')

		)
