mapper.select:
select *
	from qna_tb b, user_tb u
	where b.userIndex = u.userIndex
	  and u.userIndex = #{userIndex}
	  order by b.qnaWdate desc
	  limit #{start}, #{perPage}

mapper.insert:

	insert into qna_tb(
	
		qnaCnt 
		, qnaTitle 
		, userIndex 
		)values(
		#{qnaCnt} 
		, #{qnaTitle} 
		, #{userIndex} 
		)
		
	
		select max(qnaIndex) from qna_tb

mapper.update:
update qna_tb
	set qnaCnt = #{qnaCnt}
	, qnaTitle = #{qnaTitle}
	where qnaIndex = #{qnaIndex}

update qna_tb
	set qnaRsp = #{qnaRsp}
	where qnaIndex = #{qnaIndex}
